00646 void DIOESP32STREAMSPI::ThreadConnection(void* param)
00647 {
00648   DIOESP32STREAMSPI* diostream = (DIOESP32STREAMSPI*)param;
00649   if(!diostream) return;
00650 
00651   //HAL_NVIC_DisableIRQ(TIM3_IRQn);
00652 
00653   if(diostream->GetEvent()==DIOSTREAMSPI_FSMEVENT_NONE) // No hay nuevos Eventos
00654     {
00655       switch(diostream->GetCurrentState())
00656         {
00657           case DIOSTREAMSPI_FSMSTATE_NONE                   : break;
00658 
00659           case DIOSTREAMSPI_FSMSTATE_CONNECTED              : break;
00660 
00661           case DIOSTREAMSPI_FSMSTATE_WAITINGTOREAD          : if(!diostream->IsBlockRead())
00662                                                                 {
00663                                                                   if(!diostream->inbuffer->IsBlocked())
00664                                                                     {
00665                                                                       if(diostream->readcache[diostream->indexport].GetSize())
00666                                                                         {
00667                                                                           diostream->inbuffer->Add(diostream->readcache[diostream->indexport]);
00668                                                                           diostream->readcache[diostream->indexport].Delete();
00669                                                                         }
00670                                                                     }
00671                                                                 }
00672 
00673                                                               if(!diostream->IsBlockWrite())
00674                                                                 {
00675                                                                   if(!diostream->outbuffer->IsBlocked())
00676                                                                     {
00677                                                                       int esize = diostream->outbuffer->GetSize();
00678                                                                       if(esize>DIOSTREAM_MAXBUFFER) esize = DIOSTREAM_MAXBUFFER;
00679 
00680                                                                       if(esize)
00681                                                                         {
00682                                                                           diostream->outbuffer->Extract(diostream->writebuffer, 0, esize);
00683                                                                           diostream->WriteDirect(diostream->writebuffer, esize);
00684                                                                         }
00685                                                                     }
00686                                                                 }
00687                                                               break;
00688 
00689           case DIOSTREAMSPI_FSMSTATE_DISCONNECTING          : break;
00690 
00691         }
00692     }
00693    else
00694     {
00695       if(diostream->GetEvent()<DIOSTREAMSPI_LASTEVENT)
00696         {
00697           diostream->CheckTransition();
00698 
00699           switch(diostream->GetCurrentState())
00700             {
00701               case DIOSTREAMSPI_FSMSTATE_NONE               : break;
00702 
00703               case DIOSTREAMSPI_FSMSTATE_CONNECTED          : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_CONNECTED);
00704                                                                 xevent.SetDIOStream(diostream);
00705                                                                 diostream->PostEvent(&xevent);
00706 
00707                                                                 diostream->SetEvent(DIOSTREAMSPI_FSMEVENT_WAITINGTOREAD);
00708                                                               }
00709                                                               break;
00710 
00711               case DIOSTREAMSPI_FSMSTATE_WAITINGTOREAD      : break;
00712 
00713               case DIOSTREAMSPI_FSMSTATE_DISCONNECTING      : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_DISCONNECTED);
00714                                                                 xevent.SetDIOStream(diostream);
00715                                                                 diostream->PostEvent(&xevent);
