00415 bool DIOI2C3DACCELEROMETERLSM303DLHC::Configure()
00416 {
00417   if(!IsInitialized()) return false;
00418 
00419   XBYTE value;
00420 
00421   value  = 0x00;
00422   value |= 0x01 << 5;     // Normal mode
00423   value |= 0x07;          // X/Y/Z axis enable.
00424   if(!WriteRegister(DIOI2CLSM303DLHC_CTRL_REG1_A , value))    return false;
00425   value  = 0x00;
00426   ReadRegister(DIOI2CLSM303DLHC_CTRL_REG1_A, value);
00427 
00428   value  = 0x00;
00429   value |= 0x01 << 6;     // 1: data MSB @ lower address
00430   value |= 0x01 << 4;     // +/- 4g
00431   if(!WriteRegister(DIOI2CLSM303DLHC_CTRL_REG4_A, value))   return false;
00432 
00433   return true;
00434 }
