00513 {
00514   if(!IsInitialized()) return false;
00515 
00516   XBYTE value;
00517 
00518   value  = 0x00;
00519   value |= 0x04 << 2;     // Minimum data output rate = 15Hz
00520   if(!WriteRegister(DIOI2CLSM303DLHC_CRA_REG_M, value))           return false;
00521 
00522   value  = 0x00;
00523 //value |= 0x01 << 5;     // +-1.3Gauss
00524   value |= 0x07 << 5;     // +-8.1Gauss
00525   if(!WriteRegister(DIOI2CLSM303DLHC_CRB_REG_M, value))           return false;
00526 
00527   value = 0x00;           // Continuous-conversion mode
00528   if(!WriteRegister(DIOI2CLSM303DLHC_MR_REG_M, value))            return false;
00529 
00530   return true;
