00750 bool DIOI2C6AXISTRACKINGLSM303DLHC::Read(DIOI2CLSM303DLHCVECTOR& a, DIOI2CLSM303DLHCVECTOR& c)
00751 {
00752   if(!accelerometer)  return false;
00753   if(!compass)        return false;
00754 
00755   XWORD ax = 0;
00756   XWORD ay = 0;
00757   XWORD az = 0;
00758   XWORD cx = 0;
00759   XWORD cy = 0;
00760   XWORD cz = 0;
00761 
00762   if(!Read(ax, ay, az, cx, cy, cz)) return false;
00763 
00764   // Perform simple lowpass filtering Intended to stabilize heading despite device vibration such as on a UGV
00765   filter_ax += ax - (filter_ax >> DIOI2CLSM303DLHC_FILTERSHIFT);
00766   filter_ay += ay - (filter_ay >> DIOI2CLSM303DLHC_FILTERSHIFT);
00767   filter_az += az - (filter_az >> DIOI2CLSM303DLHC_FILTERSHIFT);
00768 
00769   a.x = (float)(filter_ax >> DIOI2CLSM303DLHC_FILTERSHIFT);
00770   a.y = (float)(filter_ay >> DIOI2CLSM303DLHC_FILTERSHIFT);
00771   a.z = (float)(filter_az >> DIOI2CLSM303DLHC_FILTERSHIFT);
00772 
00773   // offset and scale
00774   c.x = (cx + offset_x) * scale_x;
00775   c.y = (cy + offset_y) * scale_y;
00776   c.z = (cz + offset_z) * scale_z;
00777 
00778   return true;
00779 }
