00814 float DIOI2C6AXISTRACKINGLSM303DLHC::Heading(DIOI2CLSM303DLHCVECTOR& from)
00815 {
00816   DIOI2CLSM303DLHCVECTOR a;
00817   DIOI2CLSM303DLHCVECTOR c;
00818 
00819   if(!Read(a, c)) return 0.0f;
00820 
00821   DIOI2CLSM303DLHCVECTOR temp_a;
00822 
00823   temp_a.x = a.x;
00824   temp_a.y = a.y;
00825   temp_a.z = a.z;
00826 
00827   // normalize
00828   DIOI2CLSM303DLHCVECTOR::Normalize(temp_a);
00829 
00830   // compute E and N
00831   DIOI2CLSM303DLHCVECTOR e;
00832   DIOI2CLSM303DLHCVECTOR n;
00833 
00834   DIOI2CLSM303DLHCVECTOR::Cross(c ,temp_a, e);
00835 
00836   DIOI2CLSM303DLHCVECTOR::Normalize(e);
00837 
00838   DIOI2CLSM303DLHCVECTOR::Cross(temp_a, e , n);
00839 
00840   // compute heading
00841   float heading = (float)atan2( DIOI2CLSM303DLHCVECTOR::Dot(e , from), DIOI2CLSM303DLHCVECTOR::Dot(n , from)) * (180/PI);
00842   if (heading < 0) heading += 360;
00843 
00844   return float(heading);
00845 }
