00128 bool DIOI2C9AXISTRACKINGMPU9150::IniDevice()
00129 {
00130   if(!DIODEVICEI2C::IniDevice()) return false;
00131 
00132   if(diostream->Open())
00133     {
00134       if(diostream->WaitToConnected(timeout))
00135         {
00136           return DIODEVICE::Ini();
00137         }
00138     }
00139 
00140   return false;
00141 }
