00153 void DIOI2C9AXISTRACKINGMPU9150::Initialize()
00154 {
00155   SetClockSource(DIOI2CMPU9150_CLOCK_PLL_XGYRO);
00156   SetFullScaleGyroRange(DIOI2CMPU9150_GYRO_FS_250);
00157   SetFullScaleAccelRange(DIOI2CMPU9150_ACCEL_FS_2);
00158   SetSleepEnabled(false); // thanks to Jack Elston for pointing this one out!
00159 }
