03167 
03168 /*-------------------------------------------------------------------
03169 //  DIOI2C9AXISTRACKINGMPU9150::GetMotion9
03170 */
03188 /*-----------------------------------------------------------------*/
03189 void DIOI2C9AXISTRACKINGMPU9150::GetMotion9(XWORDSIG* ax, XWORDSIG* ay, XWORDSIG* az, XWORDSIG* gx, XWORDSIG* gy, XWORDSIG* gz, XWORDSIG* mx, XWORDSIG* my, XWORDSIG* mz)
03190 {
03191   //get accel and gyro
03192   GetMotion6(ax, ay, az, gx, gy, gz);
03193 
03194   //read mag
03195   WriteByte(deviceaddr, DIOI2CMPU9150_RA_INT_PIN_CFG, 0x02); //set i2c bypass enable pin to true to access magnetometer
03196   GEN_XSLEEP.MilliSeconds(10);
03197 
03198   WriteByte(DIOI2CMPU9150_RA_MAG_ADDRESS, 0x0A, 0x01); //enable the magnetometer
03199   GEN_XSLEEP.MilliSeconds(10);
03200 
03201   ReadBytes(DIOI2CMPU9150_RA_MAG_ADDRESS, DIOI2CMPU9150_RA_MAG_XOUT_L, 6, buffer);
03202   *mx = (((XWORDSIG)buffer[1]) << 8) | buffer[0];
03203   *my = (((XWORDSIG)buffer[3]) << 8) | buffer[2];
03204   *mz = (((XWORDSIG)buffer[5]) << 8) | buffer[4];
03205 }
03206 
03207 
03208 
03209 /*-------------------------------------------------------------------
03210 //  DIOI2C9AXISTRACKINGMPU9150::GetMag
03211 */
03223 /*-----------------------------------------------------------------*/
03224 void DIOI2C9AXISTRACKINGMPU9150::GetMag(XWORDSIG* mx, XWORDSIG* my, XWORDSIG* mz)
03225 {
03226   //read mag
03227   WriteByte(deviceaddr, DIOI2CMPU9150_RA_INT_PIN_CFG, 0x02); //set i2c bypass enable pin to true to access magnetometer
03228   GEN_XSLEEP.MilliSeconds(10);
03229 
03230   WriteByte(DIOI2CMPU9150_RA_MAG_ADDRESS, 0x0A, 0x01); //enable the magnetometer
03231   GEN_XSLEEP.MilliSeconds(10);
03232 
03233   ReadBytes(DIOI2CMPU9150_RA_MAG_ADDRESS, DIOI2CMPU9150_RA_MAG_XOUT_L, 6, buffer);
03234   *mx = (((XWORDSIG)buffer[1]) << 8) | buffer[0];
03235   *my = (((XWORDSIG)buffer[3]) << 8) | buffer[2];
03236   *mz = (((XWORDSIG)buffer[5]) << 8) | buffer[4];
03237 }
03238 
03239 
03240 
03241 
03242 /*-------------------------------------------------------------------
03243 //  DIOI2C9AXISTRACKINGMPU9150::GetMotion6
03244 */
03259 /*-----------------------------------------------------------------*/
03260 void DIOI2C9AXISTRACKINGMPU9150::GetMotion6(XWORDSIG* ax, XWORDSIG* ay, XWORDSIG* az, XWORDSIG* gx, XWORDSIG* gy, XWORDSIG* gz)
03261 {
03262   ReadBytes(deviceaddr, DIOI2CMPU9150_RA_ACCEL_XOUT_H, 14, buffer);
03263   *ax = (((XWORDSIG)buffer[0])  << 8) | buffer[1];
03264   *ay = (((XWORDSIG)buffer[2])  << 8) | buffer[3];
03265   *az = (((XWORDSIG)buffer[4])  << 8) | buffer[5];
03266   *gx = (((XWORDSIG)buffer[8])  << 8) | buffer[9];
03267   *gy = (((XWORDSIG)buffer[10]) << 8) | buffer[11];
03268   *gz = (((XWORDSIG)buffer[12]) << 8) | buffer[13];
03269 }
03270 
