03898 /*-------------------------------------------------------------------
03899 //  DIOI2C9AXISTRACKINGMPU9150::GetSlaveDelayEnabled
03900 */
03923 /*-----------------------------------------------------------------*/
03924 bool DIOI2C9AXISTRACKINGMPU9150::GetSlaveDelayEnabled(XBYTE num)
03925 {
03926   // DIOI2CMPU9150_DELAYCTRL_I2C_SLV4_DLY_EN_BIT is 4, SLV3 is 3, etc.
03927   if(num > 4) return false;
03928   ReadBit(deviceaddr, DIOI2CMPU9150_RA_I2C_MST_DELAY_CTRL, num, buffer);
03929   return buffer[0]?true:false;
03930 }
