04936 /*-----------------------------------------------------------------*/
04937 XWORDSIG DIOI2C9AXISTRACKINGMPU9150::GetFIFOCount()
04938 {
04939   ReadBytes(deviceaddr, DIOI2CMPU9150_RA_FIFO_COUNTH, 2, buffer);
04940   return (((XWORDSIG)buffer[0]) << 8) | buffer[1];
04941 }
