05725 void DIOI2C9AXISTRACKINGMPU9150::SetMemoryBank(XBYTE bank, bool prefetchEnabled, bool userBank)
05726 {
05727   bank &= 0x1F;
05728 
05729   if(userBank)        bank |= 0x20;
05730   if(prefetchEnabled) bank |= 0x40;
05731 
05732   WriteByte(deviceaddr, DIOI2CMPU9150_RA_BANK_SEL, bank);
05733 }
