00385 bool DIOI2CAIRQUALITYCCS811::SetDriveMode(XBYTE mode)
00386 {
00387   if(mode > 4) mode = 4; // sanitize input
00388 
00389   XBYTE value = 0;
00390 
00391   bool status = ReadRegister(DIOI2CAIRQUALITYCCS811_OFFSET_MEAS_MODE, value);   //  Read what's currently there
00392   if(!status) return false;
00393 
00394   value &= ~(0x03 << 4);  //  Clear DRIVE_MODE bits
00395   value |=  (mode << 4);  //  Mask in mode
00396 
00397   status = WriteRegister(DIOI2CAIRQUALITYCCS811_OFFSET_MEAS_MODE, value);
00398 
00399   return status;
00400 }
