00415 bool DIOI2CAIRQUALITYCCS811::SetEnvironmentalData(float relativehumidity, float temperature)
00416 {
00417   if((temperature < -25)    || (temperature > 50))        return false;
00418   if((relativehumidity < 0) || (relativehumidity > 100))  return false;
00419 
00420   XDWORD rhum   = (XDWORD)(relativehumidity * 1000);  //42.348 becomes 42348
00421   XDWORD temp   = (XDWORD)(temperature      * 1000);  //23.2 becomes 23200
00422 
00423   XBYTE envdata[4];
00424 
00425   //Split value into 7-bit integer and 9-bit fractional
00426 
00427   //Incorrect way from datasheet.
00428   //envData[0] = ((rH % 1000) / 100) > 7 ? (rH / 1000 + 1) << 1 : (rH / 1000) << 1;
00429   //envData[1] = 0; //CCS811 only supports increments of 0.5 so bits 7-0 will always be zero
00430   //if (((rH % 1000) / 100) > 2 && (((rH % 1000) / 100) < 8))
00431   //{
00432   //  envData[0] |= 1; //Set 9th bit of fractional to indicate 0.5%
00433   //}
00434 
00435   //Correct rounding. See issue 8: https://github.com/sparkfun/Qwiic_BME280_CCS811_Combo/issues/8
00436   envdata[0] = (rhum + 250) / 500;
00437   envdata[1] = 0;                   //CCS811 only supports increments of 0.5 so bits 7-0 will always be zero
00438 
00439   temp += 25000; //Add the 25C offset
00440 
00441   //Split value into 7-bit integer and 9-bit fractional
00442   //envData[2] = ((temp % 1000) / 100) > 7 ? (temp / 1000 + 1) << 1 : (temp / 1000) << 1;
00443   //envData[3] = 0;
00444   //if (((temp % 1000) / 100) > 2 && (((temp % 1000) / 100) < 8))
00445   //{
00446   //  envData[2] |= 1;  //Set 9th bit of fractional to indicate 0.5C
00447   //}
00448 
00449   //Correct rounding
00450   envdata[2] = (temp + 250) / 500;
00451   envdata[3] = 0;
00452 
00453   return WriteRegister(DIOI2CAIRQUALITYCCS811_OFFSET_ENV_DATA, envdata, sizeof(envdata));
00454 }
