00501 bool DIOI2CAIRQUALITYCCS811::ReadNTC(float& temperature, float& resistance)
00502 {
00503   XBYTE data[4];
00504   XWORD vrefcounts = 0;
00505   XWORD ntccounts  = 0;
00506 
00507   memset(data, 0, sizeof(data));
00508 
00509   bool status = ReadRegister(DIOI2CAIRQUALITYCCS811_OFFSET_NTC, data, sizeof(data));
00510 
00511   vrefcounts  = ((XWORD)data[0] << 8) | data[1];
00512   ntccounts   = ((XWORD)data[2] << 8) | data[3];
00513   resistance  = ((float)ntccounts * referenceresistance / (float)vrefcounts);
00514 
00515   temperature = (float)log((long)resistance);
00516   temperature = (float)(1 / (0.001129148 + (0.000234125 * temperature) + (0.0000000876741 * temperature * temperature * temperature)));
00517   temperature = (float)(temperature - 273.15);  // Convert Kelvin to Celsius
00518 
00519   return status;
00520 }
