00618 bool DIOI2CAIRQUALITYCCS811::IniDevice()
00619 {
00620   if(!DIODEVICEI2C::IniDevice()) return false;
00621 
00622   if(diostream->Open())
00623     {
00624       if(diostream->WaitToConnected(timeout))
00625         {
00626           XBYTE ID = 0;
