00675 bool DIOI2CAIRQUALITYCCS811::ReadRegister(XBYTE offset, XBYTE* data, XDWORD sizedata)
00676 {
00677   if(!diostream) return false;
00678 
00679   bool status;
00680 
00681   status = diostream->Write(&offset, 1)?true:false;
00682   if(status) status = diostream->WaitToFlushOutXBuffer(timeout);
00683 
00684   if(!status) return false;
00685 
00686   status = diostream->WaitToFilledReadingBuffer(sizedata, timeout);
00687   if(status) status = (diostream->Read(data, sizedata) == sizedata)?true:false;
00688 
00689   return status;
00690 }
