00724 bool DIOI2CAIRQUALITYCCS811::WriteRegister(XBYTE offset, XBYTE* data, XDWORD sizedata)
00725 {
00726   if(!diostream) return false;
00727 
00728   XBUFFER buffer;
00729   bool    status;
00730 
00731   buffer.Add((XBYTE)offset);
00732   if(data && sizedata) buffer.Add(data, sizedata);
00733 
00734   status = diostream->Write(buffer)?true:false;
00735   if(status) status = diostream->WaitToFlushOutXBuffer(timeout);
00736 
