00156 void DIOI2CBATTERYCHARGERBQ24295::Update()
00157 {  
00158   if(!charging) return;
00159             
00160   // For an unknown reason the stat pin generates an square signal and not holds the low level when is charging
00161   // so I have to read the register to know when the charge is done
00162   //I read the register every 5 seconds
00163     
00164   XBYTE read_reg_fault;        
00165   XBYTE read_reg_status;
00166       
00167   if(ReadRegister(DIOI2CBATTERYCHARGERBQ24295_FAULT_REG_REG09   , read_reg_fault)  != 0)   return;            //TODO-GFR detect FAULT errors        
00168   if(ReadRegister(DIOI2CBATTERYCHARGERBQ24295_SYS_STATUS_REG08  , read_reg_status) != 0)   return;            
00169         
00170   if(((read_reg_status >> 4) & 0x03) == 0x03) fullycharge = true;          
00171 }
