00332 bool DIOI2CBATTERYCHARGERBQ24295::Initialize()
00333 {
00334   // Charger GPIO Inputs
00335   GEN_DIOGPIO.SetMode(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_STAT]  , DIOGPIO_MODE_INPUT);          
00336             
00337   // Charger GPIO Outputs
00338   GEN_DIOGPIO.SetMode(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_QON]   , DIOGPIO_MODE_OUTPUT);
00339   GEN_DIOGPIO.SetMode(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_CE]    , DIOGPIO_MODE_OUTPUT);
00340           
00341   // Charger GPIO Interrupts
00342   DIOGPIO_ENTRY* interrupt = GEN_DIOGPIO.GPIOEntry_GetByID(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_INT]);
00343   if(interrupt)
00344     {
00345       interrupt->SetIntFunctionPointer(DIOI2CBATTERYCHARGERBQ24295::Handler_Interrupt);      
00346       interrupt->SetIntParamPointer((void*)this);      
00347       GEN_DIOGPIO.SetMode(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_INT], DIOGPIO_MODE_INTERRUPT); 
00348     }
00349           
00350   // Charger disabled
00351   GEN_DIOGPIO.SetValue(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_CE]  , true);                     
00352           
00353   // Shipping Mode off
00354   GEN_DIOGPIO.SetValue(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_QON]  , false);           
00355           
00356   charging    = false;
00357   fault       = 0;
00358   fullycharge = false;
00359           
00360   // Configure the charger ILMAX to 500mA  
00361   XBYTE read_reg;
00362 
00363   if(!ReadRegister(DIOI2CBATTERYCHARGERBQ24295_INPUT_SRC_CTRL_REG00, read_reg))         return false;
00364           
00365   // IINLIM bits (010) (500mA)
00366   read_reg &= 0xF8;
00367   read_reg |= 0x02;
00368           
00369   if(!WriteRegister(DIOI2CBATTERYCHARGERBQ24295_INPUT_SRC_CTRL_REG00, read_reg))        return false;
00370                    
00371   // Disable the charger watchdog timer and the safety timer
00372   if(!ReadRegister(DIOI2CBATTERYCHARGERBQ24295_CHRG_TERM_TIMER_CTRL_REG05, read_reg))   return false;
00373           
00374   // Disable timer
00375   read_reg &= 0xC7;
00376   if(!WriteRegister(DIOI2CBATTERYCHARGERBQ24295_INPUT_SRC_CTRL_REG00, read_reg))        return false;          
00377                                 
00378   return true;  
00379 }
