00414 bool DIOI2CBATTERYCHARGERBQ24295::ReadRegister(XBYTE offset, XBYTE* data, XDWORD sizedata)
00415 {
00416   if(!diostream) return false;
00417 
00418   bool status;
00419 
00420   status = diostream->Write(&offset, 1)?true:false;
00421   if(status) status = diostream->WaitToFlushOutXBuffer(timeout);
00422 
00423   if(!status) return false;
00424 
00425   status = diostream->WaitToFilledReadingBuffer(sizedata, timeout);
00426   if(status) status = (diostream->Read(data, sizedata) == sizedata)?true:false;
00427 
00428   return status;
00429 }
