00463 bool DIOI2CBATTERYCHARGERBQ24295::WriteRegister(XBYTE offset, XBYTE* data, XDWORD sizedata)
00464 {
00465   if(!diostream) return false;
00466 
00467   XBUFFER buffer;
00468   bool    status;
00469 
00470   buffer.Add((XBYTE)offset);
00471   if(data && sizedata) buffer.Add(data, sizedata);
00472 
00473   status = diostream->Write(buffer)?true:false;
00474   if(status) status = diostream->WaitToFlushOutXBuffer(timeout);
00475 
00476   return status;
00477 }
