00158 bool DIOI2CEEPROM24XXX::Read(XDWORD address, XBYTE* buffer, XDWORD size)
00159 {
00160   if(!diostream)        return false;
00161   if(!IsInitialized())  return false;
00162 
00163   if(diostreamcfg->GetRemoteAddress() ==  DIOSTREAMI2CCONFIG_ADDRESSINVALID) return false;
00164 
00165   if((address+size)>maxsize) return false;
00166 
00167   bool status = true;
00168 
00169   for(XDWORD c=0; c<size; c++)
00170     {
00171       xtimerout->Reset();
00172 
00173       while(1)
00174         {
00175           status = ReadBYTE(address+c, buffer[c]);
00176           if(!status)
00177             {
00178               if(xtimerout->GetMeasureSeconds()>(XDWORD)timeout) break;
00179 
00180             } else break;
00181         }
00182     }
00183 
00184   return status;
00185 }
