00168 bool DIOI2CLIGHTSENSORTSL2561::Get(XWORD& fullspectrum, XWORD& infrared, XDWORD& lux, bool autogain)
00169 {
00170   fullspectrum = 0;
00171   infrared     = 0;
00172   lux          = 0;
00173 
00174   if(!diostream) return false;
00175 
00176   XWORD fs, ir, hi, lo;
00177   bool  status = true;
00178 
00179   if(!autogain)
00180     {
00181       // autogain not requested. Executing a single sensor read
00182 
00183       status = GetData(fullspectrum, infrared);
00184       lux = CalculateLux(fullspectrum, infrared);
00185 
00186       return status;
00187     }
00188 
00189   // autogain requested
00190   switch(integrationtime)
00191     {
00192       case DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_13MS   : hi = DIOI2CLIGHTSENSORTSL2561_AGC_THI_13MS;
00193                                                             lo = DIOI2CLIGHTSENSORTSL2561_AGC_TLO_13MS;
00194                                                             break;
00195 
00196       case DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_101MS  : hi = DIOI2CLIGHTSENSORTSL2561_AGC_THI_101MS;
00197                                                             lo = DIOI2CLIGHTSENSORTSL2561_AGC_TLO_101MS;
00198                                                             break;
00199 
00200       case DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_402MS  : hi = DIOI2CLIGHTSENSORTSL2561_AGC_THI_402MS;
00201                                                             lo = DIOI2CLIGHTSENSORTSL2561_AGC_TLO_402MS;
00202                                                             break;
00203 
00204                                               default     : hi = DIOI2CLIGHTSENSORTSL2561_AGC_THI_402MS;
00205                                                             lo = DIOI2CLIGHTSENSORTSL2561_AGC_TLO_402MS;
00206                                                             break;
00207     }
00208 
00209   // try to adjust the gain
00210   status = GetData(fs, ir);
00211   if(!status) return false;
00212 
00213   if((fs < lo) && (gain == DIOI2CLIGHTSENSORTSL2561GAIN_1X))
00214     {
00215       // light too low with this gain
00216       // raise the gain and redo the reading
00217       SetGain(DIOI2CLIGHTSENSORTSL2561GAIN_16X);
00218 
00219       status = GetData(fs, ir);
00220       if(!status) return false;
00221 
00222       // now consider the reading valid after being adjusted
00223       fullspectrum = fs;
00224       infrared     = ir;
00225 
00226       lux = CalculateLux(fullspectrum, infrared);
00227 
00228       return true;
00229     }
00230 
00231 
00232   if((fs > hi) && (gain == DIOI2CLIGHTSENSORTSL2561GAIN_16X))
00233     {
00234       // light too high with this gain
00235       // lower the gain and redo the reading
00236       SetGain(DIOI2CLIGHTSENSORTSL2561GAIN_1X);
00237 
00238       status = GetData(fs, ir);
00239       if(!status) return false;
00240 
00241       // now consider the reading valid after being adjusted
00242       fullspectrum = fs;
00243       infrared     = ir;
00244       lux = CalculateLux(fullspectrum, infrared);
00245 
00246       return true;
00247     }
00248 
00249 
00250   // the reading was valid without gain adjustment (or chip limits encountered!)
00251   fullspectrum = fs;
00252   infrared     = ir;
00253 
00254   lux = CalculateLux(fullspectrum, infrared);
00255 
00256   return true;
00257 }
