00286 bool DIOI2CLIGHTSENSORTSL2561::IniDevice()
00287 {
00288   if(!DIODEVICEI2C::IniDevice()) return false;
00289 
00290   if(diostream->Open())
00291     {
00292       if(diostream->WaitToConnected(timeout))
00293         {
00294           bool status;
00295                     
00296           status = SetIntegrationTime(DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_402MS);
00297           if(!status) return false;
00298           
00299           status = SetGain(DIOI2CLIGHTSENSORTSL2561GAIN_16X);
00300           if(!status) return false;
00301                     
00302           return DIODEVICE::Ini();
00303         }
00304     }
00305 
00306   return false;
00307 }
