00352 bool DIOI2CLIGHTSENSORTSL2561::ReadData16(XBYTE reg, XWORD& value, int timeout)
00353 {
00354   XBUFFER xbuffer;
00355 
00356   value = 0;
00357 
00358   xbuffer.Add((XBYTE)reg);
00359   if(!diostream->Write(xbuffer))                              return false;
00360   if(!diostream->WaitToFlushOutXBuffer(timeout))              return false;
00361 
00362   xbuffer.Resize(2);
00363   if(!diostream->WaitToFilledReadingBuffer(2, timeout))       return false;
00364   if(!diostream->Read(xbuffer))                               return false;
00365   if(xbuffer.GetSize() < 2)                                   return false;
00366 
00367   value   = xbuffer.GetByte(1);
00368   value <<= 8;
00369   value  |= xbuffer.GetByte(0);
00370 
00371   //XTRACE_PRINTCOLOR(XDEBUG_COLORGREEN, __L("%02X %02X"), xbuffer.GetByte(1), xbuffer.GetByte(0));
00372 
00373   return true;
00374 }
