00389 bool DIOI2CLIGHTSENSORTSL2561::GetData(XWORD& fullspectrum, XWORD& infrared)
00390 {
00391   fullspectrum = 0;
00392   infrared     = 0;
00393 
00394   if(!Activate()) return false;
00395 
00396   bool status;
00397 
00398   // wait for the internal ADC to complete conversion
00399   switch(integrationtime)
00400     {
00401       case DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_13MS  : GEN_XSLEEP.MilliSeconds(20);  break;
00402       case DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_101MS : GEN_XSLEEP.MilliSeconds(150); break;
00403       case DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_402MS : GEN_XSLEEP.MilliSeconds(450); break;
00404     }
00405 
00406   status = ReadData16((DIOI2CLIGHTSENSORTSL2561_COMMAND_BIT |  DIOI2CLIGHTSENSORTSL2561_WORD_BIT |  DIOI2CLIGHTSENSORTSL2561REGISTER_CHAN0_LOW), fullspectrum, timeout);
00407   if(status) status = ReadData16((DIOI2CLIGHTSENSORTSL2561_COMMAND_BIT |  DIOI2CLIGHTSENSORTSL2561_WORD_BIT |  DIOI2CLIGHTSENSORTSL2561REGISTER_CHAN1_LOW), infrared    , timeout);
00408 
00409   if(status) status = Deactivate();
00410 
00411   return status;
00412 }
