00457 XDWORD DIOI2CLIGHTSENSORTSL2561::CalculateLux(XWORD broadband, XWORD inflared)
00458 {
00459   XDWORD chscale;
00460   XDWORD channel1;
00461   XDWORD channel0;
00462   XDWORD clipthreshold;
00463   XDWORD ratio1 = 0;
00464   int    ratio;
00465   XDWORD b, m;
00466   int    temp;
00467   XDWORD lux;
00468 
00469   // Make sure the sensor isn't saturated!
00470   switch(integrationtime)
00471     {
00472       case DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_13MS   : clipthreshold = DIOI2CLIGHTSENSORTSL2561_CLIPPING_13MS;   break;
00473       case DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_101MS  : clipthreshold = DIOI2CLIGHTSENSORTSL2561_CLIPPING_101MS;  break;
00474       case DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_402MS  : clipthreshold = DIOI2CLIGHTSENSORTSL2561_CLIPPING_402MS;  break;
00475                                               default     : clipthreshold = DIOI2CLIGHTSENSORTSL2561_CLIPPING_402MS;  break;
00476     }
00477 
00478   // return 0 lux if the sensor is saturated
00479   if((broadband > clipthreshold) || (inflared > clipthreshold)) return 0;
00480 
00481 
00482   // get the correct scale depending on the intergration time
00483   switch(integrationtime)
00484     {
00485       case DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_13MS   : chscale = DIOI2CLIGHTSENSORTSL2561_LUX_CHSCALE_TINT0;   break;
00486       case DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_101MS  : chscale = DIOI2CLIGHTSENSORTSL2561_LUX_CHSCALE_TINT1;   break;
00487       case DIOI2CLIGHTSENSORTSL2561INTEGRATIONTIME_402MS  : chscale = (1 << DIOI2CLIGHTSENSORTSL2561_LUX_CHSCALE);  break;
00488                                                   default : chscale = (1 << DIOI2CLIGHTSENSORTSL2561_LUX_CHSCALE);  break;   // No scaling ... integration time = 402ms
00489   }
00490 
00491   // scale for gain (1x or 16x)
00492   if (!gain) chscale = chscale << 4;
00493 
00494   // scale the channel values
00495   channel0 = (broadband * chscale) >> DIOI2CLIGHTSENSORTSL2561_LUX_CHSCALE;
00496   channel1 = (inflared  * chscale) >> DIOI2CLIGHTSENSORTSL2561_LUX_CHSCALE;
00497 
00498   // find the ratio of the channel values (Channel1/Channel0)
00499   if (channel0 != 0) ratio1 = (channel1 << (DIOI2CLIGHTSENSORTSL2561_LUX_RATIOSCALE+1)) / channel0;
00500 
00501   // round the ratio value
00502   ratio = (ratio1 + 1) >> 1;
00503 
00504   #ifdef TSL2561_PACKAGE_CS
00505   if ((ratio >= 0) && (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K1C))
00506     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B1C;  m = DIOI2CLIGHTSENSORTSL2561_LUX_M1C;  }
00507    else if (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K2C)
00508     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B2C;  m = DIOI2CLIGHTSENSORTSL2561_LUX_M2C;  }
00509    else if (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K3C)
00510     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B3C;  m = DIOI2CLIGHTSENSORTSL2561_LUX_M3C;  }
00511    else if (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K4C)
00512     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B4C;  m = DIOI2CLIGHTSENSORTSL2561_LUX_M4C;  }
00513    else if (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K5C)
00514     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B5C;  m = DIOI2CLIGHTSENSORTSL2561_LUX_M5C;  }
00515    else if (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K6C)
00516     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B6C;  m = DIOI2CLIGHTSENSORTSL2561_LUX_M6C;  }
00517    else if (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K7C)
00518     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B7C;  m = DIOI2CLIGHTSENSORTSL2561_LUX_M7C;  }
00519    else if (ratio > DIOI2CLIGHTSENSORTSL2561_LUX_K8C)
00520     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B8C;  m = DIOI2CLIGHTSENSORTSL2561_LUX_M8C;  }
00521   #else
00522   if ((ratio >= 0) && (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K1T))
00523     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B1T; m = DIOI2CLIGHTSENSORTSL2561_LUX_M1T;   }
00524    else if (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K2T)
00525     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B2T; m = DIOI2CLIGHTSENSORTSL2561_LUX_M2T;   }
00526    else if (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K3T)
00527     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B3T; m = DIOI2CLIGHTSENSORTSL2561_LUX_M3T;   }
00528    else if (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K4T)
00529     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B4T; m = DIOI2CLIGHTSENSORTSL2561_LUX_M4T;   }
00530    else if (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K5T)
00531     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B5T; m = DIOI2CLIGHTSENSORTSL2561_LUX_M5T;   }
00532    else if (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K6T)
00533     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B6T; m = DIOI2CLIGHTSENSORTSL2561_LUX_M6T;   }
00534    else if (ratio <= DIOI2CLIGHTSENSORTSL2561_LUX_K7T)
00535     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B7T; m = DIOI2CLIGHTSENSORTSL2561_LUX_M7T;   }
00536    else if (ratio > DIOI2CLIGHTSENSORTSL2561_LUX_K8T)
00537     { b = DIOI2CLIGHTSENSORTSL2561_LUX_B8T; m = DIOI2CLIGHTSENSORTSL2561_LUX_M8T;   }
00538   #endif
00539 
00540   temp = ((channel0 * b) - (channel1 * m));
00541 
00542   // do not allow negative lux value
00543   if (temp < 0) temp = 0;
00544 
00545   // round lsb (2^(LUX_SCALE-1))
00546   temp += (1 << (DIOI2CLIGHTSENSORTSL2561_LUX_LUXSCALE-1));
00547 
00548   // strip off fractional portion
00549   lux = (temp >> DIOI2CLIGHTSENSORTSL2561_LUX_LUXSCALE);
00550 
00551   // Signal I2C had no errors
00552   return lux;
00553 }
