00136 bool DIOI2COLEDDISPLAYSSD1306::IniDevice()
00137 {
00138   if(!DIODEVICEI2C::IniDevice()) return false;
00139 
00140   if(diostream->Open())
00141     {
00142       if(diostream->WaitToConnected(timeout))
00143         {
00144           SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_DISPLAY_OFF);                            // 0xAE
00145           SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_DISPLAY_CLOCK_DIV_RATIO_SET);            // 0xD5
00146           SendCommand(0xF0);                                                                    // Suggest ratio
00147           SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_MULTIPLEX_RADIO_SET);                    // 0xA8
