00151           SendCommand(00);                                                                      // No offset
00152           SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_CHARGE_PUMP_SET);                        // 0x8D
00153           SendCommand(0x14);                                                                    // Enable charge pump
00154           SendCommand((DIOI2COLEDDISPLAYSSD1306_COMMAND_DISPLAY_START_LINE_SET | 0x00));        // 0x40 | Start line
00155           SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_MEMORY_ADDRESSING_SET);                  // 0x20
00156           SendCommand(0x00);                                                                    // Horizontal Addressing - Operate like KS0108
00157           SendCommand((DIOI2COLEDDISPLAYSSD1306_COMMAND_SEGMENT_REMAP_SET | 0x01));             // 0xA0 - Left towards Right
00158           SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_COM_OUTPUT_SCAN_DEC);                    // 0xC8 - Up towards Down
00159           SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_COM_PINS_SET);                           // 0xDA
00160         //SendCommand(0x12);                                                                    // Sequential COM pin configuration
00161 
00162           if((width == 128) && (height == 64)) SendCommand(0x12);                               // Sequential COM pin configuration
00163           if((width == 128) && (height == 32)) SendCommand(0x02);                               // Sequential COM pin configuration
00164           if((width ==  96) && (height == 16)) SendCommand(0x02);                               // Sequential COM pin configuration
00165 
00166           SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_CONSTRAST_SET);                          // 0x81
00167           SendCommand(0xFF);                                                                    // 0xFF
00168           SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_PRECHARGE_PERIOD_SET);                   // 0xD9
00169           SendCommand(0xF1);                                                                    // 0xF1
00170 
00171           SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_VCOMH_DESELECT_LEVEL_SET);               // 0xDB
00172           SendCommand(0x20);                                                                    // 0x20
