00205 bool DIOI2COLEDDISPLAYSSD1306::Clear(XBYTE color)
00206 {
00207   XBYTE nblocks;
00208 
00209   nblocks = (width >> 4) * (height >> 3);
00210 
00211   // Set columns
00212   SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_COLUMN_ADDRESS_SET);     // 0x21
00213   SendCommand(0x00);                                                    // Start
00214   SendCommand(width - 1);                                               // End
00215 
00216   //Set rows
00217   SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_PAGE_ADDRESS_SET);       // 0x22
00218   SendCommand(0x00);                                                    // Start
00219   SendCommand(height - 1);                                              // End
00220 
00221   XBYTE buffer[16];
00222 
00223   memset(buffer, color, 16);
00224 
00225   //Send buffer
00226   for(XBYTE c=0; c<nblocks; c++)
00227     {
00228       SendData(buffer, 16);
00229     }
00230 
00231   return true;
00232 }
