00250   XBYTE nblocks;
00251 
00252   nblocks = (width >> 4) * (height >> 3);
00253 
00254   // Set columns
00255   SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_COLUMN_ADDRESS_SET);     // 0x21
00256   SendCommand(0x00);                                                    // Start
00257   SendCommand(width - 1);                                               // End
00258 
00259   //Set rows
00260   SendCommand(DIOI2COLEDDISPLAYSSD1306_COMMAND_PAGE_ADDRESS_SET);       // 0x22
00261   SendCommand(0x00);                                                    // Start
00262   SendCommand(height - 1);                                              // End
00263 
00264   for(XBYTE c=0; c<nblocks; c++)
00265     {
00266       SendData(buffer+(c*16), 16);
00267     }
00268 
00269   return true;
