00505 bool DIOI2COLEDDISPLAYSSD1306::Send(XBYTE control, XBYTE* data, int sizedata)
00506 {
00507   XBUFFER xbuffer;
00508 
00509   if(!diostream) return false;
00510   
00511   xbuffer.Add(control);
00512   xbuffer.Add(data, sizedata);
00513 
00514   if(!diostream->Write(xbuffer))                 return false;
00515   if(!diostream->WaitToFlushOutXBuffer(timeout)) return false;
00516 
00517   return true;
