00964 bool DIOIEC60870_5::CMD_C_AC_NA_2_IniSession(int timeout)
00965 {
00966   XBYTE retfunctioncode;
00967   bool  retisfixmsg;
00968 
00969   if(!CMD_RequestStatusLink(timeout)) return false;
00970   if(!CMD_ResetRemoteLink(timeout))   return false;
00971   if(!CMD_RequestStatusLink(timeout)) return false;
00972 
00973   XBUFFER asdu;
00974 
00975   CMD_RequestUserDataClass1(&asdu,timeout);
00976 
00977   asdu.Delete();
00978 
00979   asdu.Add((XBYTE)DIOIEC60870_5_ASDUID_C_AC_NA_2_SESSIONINI); // Identificator
00980   asdu.Add((XBYTE)1);                                         // Object Number
00981   asdu.Add((XBYTE)DIOIEC60870_5_ASDUCAUSETRANS_ACTIVATION);   // Transmision Cause
00982   asdu.Add((XBYTE)addressPM);                                 // Meter Point Address
00983   asdu.Add((XBYTE)00);                                        // Register Address  = 0
00984   asdu.Add((XBYTE)00);                                        // Register Address  = 0
00985 
00986   XDWORD _keyPM = keyPM;
00987 
00988   if(inlittleendian) SWAPDWORD(_keyPM);
00989   asdu.Add((XDWORD)_keyPM);                                   // Meter Point Key
00990 
00991   if(!SendMsgVar(DIOIEC60870_5_MASTERFUNCUSERDATA,&asdu))   return false;
00992   if(!ReadMsg(retfunctioncode,retisfixmsg,NULL,timeout))    return false;
00993 
00994   if(retfunctioncode==DIOIEC60870_5_SLAVEFUNCCONFIRMACK)
00995     {
00996       XBUFFER retasdu;
00997 
00998       if(CMD_RequestUserDataClass2(&retasdu,timeout))
00999         {
01000           XBYTE* buffer = retasdu.Get();
01001           if(!buffer) return false;
01002 
01003           if(buffer[0]!=DIOIEC60870_5_ASDUID_C_AC_NA_2_SESSIONINI)      return false;
01004           if(buffer[2]!=DIOIEC60870_5_ASDUCAUSETRANS_CONFIRMACTIVATION) return false;
01005 
01006           DIOIEC60870_5_XEVENT xevent(this,DIOIEC60870_5_XEVENT_TYPE_C_AC_NA_2_INISESSION);
01007           PostEvent(&xevent);
01008 
01009           return true;
01010         }
01011 
01012       return false;
01013     }
01014 
01015   if(retfunctioncode==DIOIEC60870_5_SLAVEFUNCNOCONFIRMACK)  return false;
01016 
01017   return false;
01018 }
