01089 bool DIOIEC60870_5::CMD_C_CI_NU_2_Init(int TM,XDATETIME& timestart,XDATETIME& timeend,int timeout)
01090 {
01091   XBYTE retfunctioncode;
01092   bool retisfixmsg;
01093 
01094   XBUFFER asdu;
01095 
01096   asdu.Add((XBYTE)DIOIEC60870_5_ASDUID_C_CI_NU_2);              // Identificator
01097   asdu.Add((XBYTE)01);                                          // Object Number
01098   asdu.Add((XBYTE)DIOIEC60870_5_ASDUCAUSETRANS_ACTIVATION);     // Transmision Cause
01099   asdu.Add((XBYTE)addressPM);                                   // Meter Point Address
01100   asdu.Add((XBYTE)0x00);                                        // Register Address
01101   asdu.Add((XBYTE)((TM==1)?0x0B:0x0C));                         // Register Address
01102   asdu.Add((XBYTE)0x01);                                        // Address of Total First
01103   asdu.Add((XBYTE)0x08);                                        // Address of Total Last
01104 
01105   XBYTE resulttime[DIOIEC60870_5_SIZEMAXTIMELABELTYPEA];
01106 
01107   SetTimeLabelTypeA(timestart,false,false,false,resulttime);    asdu.Add(resulttime,DIOIEC60870_5_SIZEMAXTIMELABELTYPEA);
01108   SetTimeLabelTypeA(timeend  ,false,false,false,resulttime);    asdu.Add(resulttime,DIOIEC60870_5_SIZEMAXTIMELABELTYPEA);
01109 
01110   if(!SendMsgVar(DIOIEC60870_5_MASTERFUNCUSERDATA,&asdu))   return false;
01111   if(!ReadMsg(retfunctioncode,retisfixmsg,NULL,timeout))    return false;
01112 
01113   if(retfunctioncode==DIOIEC60870_5_SLAVEFUNCCONFIRMACK)
01114     {
01115       XBUFFER retasdu;
01116       if(!CMD_RequestUserDataClass2(&retasdu,timeout)) return false;
01117 
01118       return true;
01119     }
01120 
01121   if(retfunctioncode==DIOIEC60870_5_SLAVEFUNCNOCONFIRMACK)  return false;
01122 
01123   return false;
01124 }
