01139 bool DIOIEC60870_5::CMD_C_CI_NU_2_Read(DIO_C_CI_NU_2_RESULT* result,int timeout)
01140 {
01141   if(!result) return false;
01142 
01143   XBUFFER retasdu;
01144 
01145   if(!CMD_RequestUserDataClass2(&retasdu,timeout)) return false;
01146 
01147   DIOIEC60870_5_XEVENT xevent(this,DIOIEC60870_5_XEVENT_TYPE_C_CI_NU_2_READVALUE);
01148   PostEvent(&xevent);
01149 
01150   XBYTE* buffer = retasdu.Get();
01151   if(!buffer) return false;
01152 
01153   bool rate   = false;
01154   bool VI     = false;
01155   bool summer = false;
01156 
01157   GetTimeLabelTypeA(&buffer[54],rate,VI,summer,(*result->GetDateTime()));
01158 
01159   for(int c=6;c<(int)retasdu.GetSize()-8;c+=6)
01160     {
01161       XDWORD value = 0;
01162 
01163       for(int d=0;d<4;d++)
01164         {
01165           value |= (XDWORD)buffer[c+1+(3-d)];
01166           if(d<3) value<<=8;
01167         }
01168 
01169       result->GetIndexValues()->Add(buffer[c]);
01170       result->GetValues()->Add(value);
01171     }
01172 
01173   return true;
01174 }
