01192 bool DIOIEC60870_5::CMD_C_CI_NU_2_ReadAllValues(int TM, XDATETIME* timestart, XDATETIME* timeend, XVECTOR<DIO_C_CI_NU_2_RESULT*>* results, int timeout)
01193 {
01194   if(!timestart) return false;
01195   if(!timeend)   return false;
01196   if(!results)   return false;
01197 
01198   bool status = true;
01199 
