01201     {
01202       if(CMD_C_CI_NU_2_Init(TM,(*timestart),(*timeend),timeout))
01203         {
01204           DIO_C_CI_NU_2_RESULT* result;
01205           bool                  status2 = true;
01206 
01207           do{ result = new DIO_C_CI_NU_2_RESULT();
01208               if(result)
01209                 {
01210                   status2 = CMD_C_CI_NU_2_Read(result, timeout);
