01409 bool DIOIEC60870_5::SetTimeLabelTypeA(XDATETIME& time, bool rate, bool VI, bool summer,XBYTE* result)
01410 {
01411   memset(result,0,DIOIEC60870_5_SIZEMAXTIMELABELTYPEA);
01412 
01413   result[0] = time.GetMinutes() & 0x00FF;       // add valid flag and genuine flag
01414   result[1] = time.GetHours()   & 0x00FF;       // add summer flag
01415   result[2] = ((time.GetDayOfWeek()%7)&0x03)<<5 | (time.GetDay() & 0x1F);
01416   result[3] = time.GetMonth()   & 0x0F;
01417   result[4] = time.GetYear() - 2000;
01418 
01419   return true;
01420 }
