01438 bool DIOIEC60870_5::SetTimeLabelTypeB(XDATETIME& time, bool rate, bool VI, bool summer,XBYTE* result)
01439 {
01440   memset(result,0,DIOIEC60870_5_SIZEMAXTIMELABELTYPEB);
01441 
01442   XDWORD m = time.GetMilliSeconds() + 1000 * time.GetSeconds();
01443 
01444   result[0] = (m & 0xFF);
01445   result[1] = (m & 0xFF00)>>8;
01446   result[2] = time.GetMinutes() & 0x00FF;       // add valid flag and genuine flag
01447   result[3] = time.GetHours()   & 0x00FF;       // add summer flag
01448   result[4] = ((time.GetDayOfWeek()%7)&0x03)<<5 | (time.GetDay() & 0x1F);
01449   result[5] = time.GetMonth()   & 0x0F;
01450   result[6] = time.GetYear() - 2000;
