01602 
01615 bool DIOIEC60870_5::SendMsgVar(XBYTE functioncode, XBUFFER* dataASDU)
01616 {
01617   if(!diostream) return false;
01618   if(diostream->GetConnectStatus()!=DIOSTREAMSTATUS_CONNECTED) return false;
01619   if(!xtimer)    return false;
01620 
01621   if(!dataASDU)  return false;
01622 
01623   XBUFFER xbuffer;
01624   XBYTE   controlfield = 0;
01625   
01626   xbuffer.Add((XBYTE)DIOIEC60870_5_MARKSTARTVAR);
01627   xbuffer.Add((XBYTE)0);
01628   xbuffer.Add((XBYTE)0);
01629   xbuffer.Add((XBYTE)DIOIEC60870_5_MARKSTARTVAR);
01630 
01631   controlfield  = 0x00;
01632   controlfield |= DIOIEC60870_5_CONTROLDIRPRIMARY;
01633 
01634   if(IsValidFrameCount(functioncode))
01635     {
01636       controlfield |= FCBstate?DIOIEC60870_5_CONTROLFCB:0x00;
01637       controlfield |= DIOIEC60870_5_CONTROLFCV;
01638 
01639       FCBstate = !FCBstate;
01640     }
01641 
01642   controlfield |= (functioncode&0x0F);
01643   xbuffer.Add(controlfield);
01644 
01645   if(!havelongaddress)
01646     {
01647       xbuffer.Add((XBYTE)addressfield);
01648     }
01649    else
01650     {
01651       XWORD _addressfield = addressfield;
01652 
01653       if(inlittleendian) SWAPWORD(_addressfield);
01654       xbuffer.Add((XWORD)_addressfield);
01655     }
01656 
01657   xbuffer.Add(dataASDU);
01658 
01659   xbuffer.Set((XBYTE)(dataASDU->GetSize()+3), 1);
01660   xbuffer.Set((XBYTE)(dataASDU->GetSize()+3), 2);
01661 
01662   xbuffer.Add((XBYTE)0);
01663   xbuffer.Add((XBYTE)DIOIEC60870_5_MARKEND);
01664 
01665   xbuffer.Set(CalculateCheckSum(xbuffer), (xbuffer.GetSize()-2));
01666 
01667   if(!diostream->Write(xbuffer.Get(),xbuffer.GetSize())) return false;
01668 
01669   return true;
01670 }
