01745 bool DIOIEC60870_5::WaitToReadMsg(XDWORD size,int timeout)
01746 {
01747   XDWORD actualsize = 0;
01748 
01749   if(!xtimer) return false;
01750   xtimer->Reset();
01751 
01752   do{
01753       if(canceloperations)                                         return false;
01754 
01755       if(!diostream)                                               return false;
01756       if(diostream->GetConnectStatus()!=DIOSTREAMSTATUS_CONNECTED) return false;
01757 
01758       actualsize = diostream->GetInXBuffer()->GetSize();
01759       if((int)xtimer->GetMeasureSeconds() >= timeout) return false;
01760 
01761       GEN_XSLEEP.MilliSeconds(10);
01762 
01763       if(actualsize >= size) return true;
01764 
01765     } while(!actualsize);
01766 
01767   if(!actualsize) return false;
01768 
01769   return true;
01770 }
