01772 
01787 bool DIOIEC60870_5::ReadMsg(XBYTE& retfunctioncode,bool& retisfixmsg,XBUFFER* retASDU,int timeout)
01788 {
01789   if(!diostream) return false;
01790   if(diostream->GetConnectStatus()!=DIOSTREAMSTATUS_CONNECTED) return false;
01791   if(!xtimer)    return false;
01792 
01793   bool status  = false;
01794 
01795   XBUFFER xbuffer((XDWORD)DIOIEC60870_5_MAXSIZE, true);
01796 
01797   if(!WaitToReadMsg(DIOIEC60870_5_MAXSIZE,timeout))
01798     {
01799       return false;
01800     }
01801 
01802   int indexbufferread = 0;
01803 
01804   xtimer->Reset();
01805 
01806   while(1)
01807     {
01808       if(canceloperations) return false;
01809 
01810       if((int)xtimer->GetMeasureSeconds()>=timeout) return false;
01811 
01812       if((indexbufferread+DIOIEC60870_5_SIZEMSGFIX)>(int)diostream->GetInXBuffer()->GetSize()) break;
01813 
01814       XBYTE* buffer = &(diostream->GetInXBuffer()->Get()[indexbufferread]);
01815 
01816       switch(buffer[0])
01817         {
01818           case DIOIEC60870_5_MARKSTARTFIX : { XBYTE checksum = CalculateCheckSum(buffer,DIOIEC60870_5_SIZEMSGFIX);
01819                                               if(checksum!=buffer[4])  break;
01820 
01821                                               XWORD _addressfield;
01822 
01823                                               _addressfield   = buffer[2];
01824                                               _addressfield <<= 8;
01825                                               _addressfield  |= buffer[3];
01826 
01827                                               if(inlittleendian) SWAPWORD(_addressfield);
01828 
01829                                               if(_addressfield != addressfield)
01830                                                 {
01831                                                   indexbufferread += DIOIEC60870_5_SIZEMSGFIX;
01832                                                   continue;
01833                                                 }
01834 
01835                                               XBYTE controlfield = buffer[1];
01836                                               if(controlfield&DIOIEC60870_5_CONTROLDIRPRIMARY)  break;
01837 
01838                                               retfunctioncode   = (controlfield&0x0f);
01839                                               retisfixmsg       = true;
01840                                               diostream->GetInXBuffer()->Extract(NULL,indexbufferread,DIOIEC60870_5_SIZEMSGFIX);
01841                                             }
01842 
01843                                             status = true;
01844                                             break;
01845 
01846           case DIOIEC60870_5_MARKSTARTVAR : { XBYTE sizeasdu  = buffer[1];
01847                                               XBYTE checksum = CalculateCheckSum(buffer,sizeasdu+4+2);
01848                                               if(checksum!=buffer[sizeasdu+4])  break;
01849 
01850                                               XWORD _addressfield;
01851 
01852                                               _addressfield   = buffer[5];
01853                                               _addressfield <<= 8;
01854                                               _addressfield  |= buffer[6];
01855 
01856                                               if(inlittleendian) SWAPWORD(_addressfield);
01857 
01858                                               if(_addressfield != addressfield)
01859                                                 {
01860                                                   indexbufferread += DIOIEC60870_5_SIZEMSGFIX;
01861                                                   continue;
01862                                                 }
01863 
01864                                               XBYTE controlfield = buffer[4];
01865                                               if(controlfield&DIOIEC60870_5_CONTROLDIRPRIMARY) break;
01866 
01867                                               retfunctioncode   = (controlfield&0x0f);
01868                                               retisfixmsg       = false;
01869 
01870                                               if(retASDU) retASDU->Add(&buffer[7],sizeasdu-3);
01871 
01872                                               diostream->GetInXBuffer()->Extract(NULL,indexbufferread,sizeasdu+4+2);
01873                                             }
01874 
01875                                             status = true;
01876                                             break;
01877         }
01878     }
01879 
01880   return status;
01881 }
