01883 
01896 XBYTE DIOIEC60870_5::CalculateCheckSum(XBYTE* buffer,XDWORD sizebuffer)
01897 {
01898   if(!buffer)       return 0;
01899 
01900   XBYTE csum    = 0;
01901   int  start  = 0;
01902   int  size   = 0;
01903 
01904   switch(buffer[0])
01905     {
01906       case DIOIEC60870_5_MARKSTARTFIX : if(buffer[4]==DIOIEC60870_5_MARKEND)
01907                                           {
01908                                             start = 1;
01909                                             size  = 2;
01910                                           }
01911 
01912                                         if(buffer[5]==DIOIEC60870_5_MARKEND)
01913                                           {
01914                                             start = 1;
01915                                             size  = 3;
01916                                           }
01917                                         break;
01918 
01919       case DIOIEC60870_5_MARKSTARTVAR : if(buffer[3]==DIOIEC60870_5_MARKSTARTVAR)
01920                                           {
01921                                             start = 4;
01922                                             size = sizebuffer-start-2;
01923                                           }
01924                                         break;
01925     }
01926 
01927 
01928   for(int c=0;c<size;c++)
01929     {
01930       csum += buffer[start+c];
01931     }
01932 
01933   return csum;
01934 }
01935 
01936 
01948 XBYTE DIOIEC60870_5::CalculateCheckSum(XBUFFER& xbuffer)
01949 {
01950   return CalculateCheckSum(xbuffer.Get(),xbuffer.GetSize());
01951 }
