01953 
01966 int DIOIEC60870_5::GetIntFromBuffer(XBYTE* buffer,XDWORD size)
01967 {
01968   XDWORD value = 0;
01969 
01970   for(XDWORD d=0; d<size; d++)
01971     {
01972       value |= (XDWORD)buffer[((size-1)-d)];
01973       if(d<(size-1)) value<<=8;
01974     }
01975 
01976   return (int)value;
01977 }
