00586 bool DIOLINUXDBUS_ARGUMENTS::GetResultInteractor(XVARIANT& result)
00587 {
00588   DBusMessageIter   iter_variant;
00589   bool              status = false;
00590 
00591   if(in)                      return false; 
00592   if(!message->GetHandler())  return false;  
00593 
00594   if(dbus_message_iter_get_arg_type(&iter) != DBUS_TYPE_VARIANT) return false;
00595 
00596   dbus_message_iter_recurse(&iter, &iter_variant);
00597 
00598   switch(dbus_message_iter_get_arg_type(&iter_variant))
00599     {   
00600       case DBUS_TYPE_UINT32         : { XDWORD value = 0;
00601                                         dbus_message_iter_get_basic(&iter_variant, &value);
00602                                         result = value;
00603                                       }
00604                                       break;    
00605 
00606       case DBUS_TYPE_STRING         : 
00607       case DBUS_TYPE_OBJECT_PATH    : { char* value_str_ptr = NULL;
00608                                         dbus_message_iter_get_basic(&iter_variant, &value_str_ptr);
00609                                         if(value_str_ptr)
00610                                           {
00611                                             result = value_str_ptr;
00612                                             status = true;
00613                                           }
00614                                       }
00615                                       break;
00616 
00617 
00618     }
00619 
00620   return status;
00621 }
