00779 bool DIOLINUXDBUS::Ini(DBusBusType type)
00780 {
00781   DBusError error;
00782 
00783   // --------------------------------------------------
00784   // initialise the errors
00785   dbus_error_init(&error);
00786 
00787   connection = dbus_bus_get(type, &error);
00788   if(!connection)
00789     {
00790       DIOLINUXDBUS::TraceError(error);
00791       return false;
00792     }
00793  
00794   connection_name = dbus_bus_get_unique_name(connection);
00795   XTRACE_PRINTCOLOR(XTRACE_COLOR_GREEN, __L("[DBUS] Ini connection [%08X] %s ... "), connection, connection_name.Get());
00796     
00797   thread_signals = CREATEXTHREAD(XTHREADGROUPID_DIOLINUXDBUS, __L("DIOLINUXDBUS::Ini"), Thread_Signals, (void*)this);
00798   if(!thread_signals) return false;
00799 
00800   return thread_signals->Ini();
00801 }
