00875 bool DIOLINUXDBUS::Signal_Add(XCHAR* type, XCHAR* name)
00876 {
00877   if(!connection) return false;
00878 
00879   XSTRING     typestr;
00880   DBusError   error;
00881   bool        status = true;
00882 
00883   if(!Signal_IsTypeAdd(type))
00884     {
00885       typestr.Format(__L("type='signal', interface='%s'"), type); 
00886 
00887       XBUFFER charstr;
00888       
00889       typestr.ConvertToASCII(charstr);      
00890       dbus_bus_add_match(connection, charstr.GetPtrChar(), &error);    // see signals from the given interface
00891       
00892       dbus_connection_flush(connection);
00893 
00894       if(dbus_error_is_set(&error)) 
00895         { 
00896           DIOLINUXDBUS::TraceError(error);
00897           status = false;   
00898         }
00899 
00900       //XTRACE_PRINTCOLOR((status?XTRACE_COLOR_BLUE:XTRACE_COLOR_RED), __L("[DBUS] Signal Add Type [%s] ..."), typestr.Get()); 
00901     }
00902 
00903   if(!status) return status;
00904   
00905   DIOLINUXDBUS_SIGNAL* signal = new DIOLINUXDBUS_SIGNAL();
00906   if(signal)
00907     {
00908       signal->GetType()->Set(type);
00909       signal->GetName()->Set(name);
00910 
00911       signals.Add(signal);    
00912 
00913     } else status = false;
00914     
00915   //XTRACE_PRINTCOLOR((status?XTRACE_COLOR_BLUE:XTRACE_COLOR_RED), __L("[DBUS] Signal Add Name [%s] ..."), name); 
00916 
00917   return status;
00918 }
