00950 bool DIOLINUXDBUS::Signal_IsTypeAdd(XCHAR* type)
00951 {
00952   if(signals.IsEmpty()) return false;
00953 
00954   for(XDWORD c=0; c<signals.GetSize(); c++)
00955     {
00956       DIOLINUXDBUS_SIGNAL* signal = signals.Get(c);
00957       if(signal)
00958         {
00959           if(!signal->GetType()->Compare(type, true)) return true;
00960         }
00961     }
00962 
00963   return false;
00964 }
