01056 void DIOLINUXDBUS::Thread_Signals(void* param)
01057 {
01058   DIOLINUXDBUS* dbus = (DIOLINUXDBUS*)param;
01059   if(!dbus) return;
01060 
01061   if(!dbus->connection)       return;
01062   if(dbus->signals.IsEmpty()) return;
01063 
01064   DBusMessage* handler_message = NULL;
01065 
01066   dbus_connection_read_write(dbus->connection, 0);
01067   handler_message = dbus_connection_pop_message(dbus->connection);
01068 
01069   if(!handler_message) 
01070     { 
01071       GEN_XSLEEP.Seconds(1);
01072       return;
01073     }
01074 
01075   //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[DBUS] Message ...")); 
01076 
01077   DIOLINUXDBUS_SIGNAL*  signal        = NULL;
01078   bool                  found_signal  = false;
01079 
01080   for(XDWORD c=0; c<dbus->signals.GetSize(); c++)
01081     {
01082       signal = dbus->signals.Get(c);
01083       if(signal)
01084         {
01085           // ---------------------------------------------------------------------------------------
01086           // check if the message is a signal from the correct interface and with the correct name
01087 
01088           XBUFFER typechar;
01089           XBUFFER namechar;
01090  
01091           (*signal->GetType()).ConvertToASCII(typechar);
01092           (*signal->GetName()).ConvertToASCII(namechar);
01093 
01094           if(dbus_message_is_signal(handler_message, typechar.GetPtrChar(), namechar.GetPtrChar()))  
01095             {
01096               found_signal = true;  
01097             }
01098           
01099           if(found_signal) 
01100             {
01101               break;
01102             }
01103            else 
01104             {
01105               signal = NULL;
01106             }
01107         }
01108     }
01109 
01110 
01111   if(found_signal && signal) 
01112     {
01113       DIOLINUXDBUS_MESSAGE message;
01114 
01115       message.SetHandler(handler_message);  
01116 
01117       DIOLINUXDBUS_XEVENT xevent(dbus, DIOLINUXDBUS_XEVENT_TYPE_SIGNAL);    
01118   
01119       xevent.SetMessage(&message);
01120       xevent.SetSignal(signal);
01121 
01122       dbus->PostEvent(&xevent);
01123 
01124       //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[DBUS] Signal produced [%s] [%s] ..."), signal->GetType()->Get(), signal->GetName()->Get());    
01125     }   
01126    else
01127     {
01128       dbus_message_unref(handler_message);
01129     }
01130 }
