00184 bool DIOLINUXGPIORASPBERRYPI::SetMode(DIOGPIO_ENTRY* entry, XWORD mode)
00185 {
00186   if(!entry) return false;
00187   if(entry->GetGPIO() == DIOGPIO_INVALID) return false;
00188 
00189   bool isinput = true;
00190 
00191   if((mode & DIOGPIO_MODE_INPUT)  == DIOGPIO_MODE_INPUT)   isinput = true;
00192   if((mode & DIOGPIO_MODE_OUTPUT) == DIOGPIO_MODE_OUTPUT)  isinput = false;
00193 
00194   return RPI_GPIOMode(entry->GetGPIO(), isinput);
00195 }
